import { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    const fetchSession = async () => {
      const { data } = await supabase.auth.getSession();
      setIsLoggedIn(!!data.session);
    };

    fetchSession();

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setIsLoggedIn(!!session);
    });

    return () => {
      listener.subscription.unsubscribe();
    };
  }, []);

  const handleLogin = async () => {
    await supabase.auth.signInWithOAuth({ provider: 'google' });
  };

  const handleLogout = async () => {
    setShowPopup(true);
    setTimeout(async () => {
      await supabase.auth.signOut();
      setIsLoggedIn(false);
      setShowPopup(false);
    }, 2000);
  };

  return (
    <div className="relative">
      {/* Navbar */}
      <nav className="w-full px-8 py-4 flex justify-between items-center bg-white shadow">
        <h1 className="text-xl font-bold text-blue-600">Job Finder</h1>

        {isLoggedIn ? (
          <button
            onClick={handleLogout}
            className="text-red-500 font-medium hover:underline"
          >
            Logout
          </button>
        ) : (
          <button
            onClick={handleLogin}
            className="text-blue-500 font-medium hover:underline"
          >
            Login with Google
          </button>
        )}
      </nav>

      {/* Fancy Logout Popup */}
      {showPopup && (
        <div className="absolute top-16 right-6 z-50 bg-white border border-gray-300 shadow-lg rounded-lg px-4 py-3 flex items-center space-x-3 animate-fade-in">
          <svg
            className="animate-spin h-5 w-5 text-blue-600"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            ></circle>
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8v4l3-3-3-3v4a8 8 0 100 16v-4l-3 3 3 3v-4a8 8 0 01-8-8z"
            ></path>
          </svg>
          <span className="text-gray-700 font-medium">Logging you out...</span>
        </div>
      )}
    </div>
  );
};

export default Navbar;
