import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Result = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) return navigate('/upload');

    setData(JSON.parse(stored));
  }, []);

  if (!data) return <div className="p-10 text-center">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-50 p-10">
      <h2 className="text-2xl font-bold mb-4">Resume Analysis Result</h2>
      <div className="bg-white p-6 rounded shadow-md max-w-xl mx-auto space-y-4">
        <p><strong>Name:</strong> {data.name}</p>
        <p><strong>Education:</strong> {data.education}</p>
        <p><strong>Experience:</strong> {data.experience}</p>
        <p><strong>Skills:</strong> {data.skills.join(', ') || 'None Detected'}</p>
        <p><strong>ATS Score:</strong> <span className="text-blue-600 font-semibold">{data.ats_score}%</span></p>

        <div className="text-sm text-gray-700">
          <p><strong>Skill Match Score:</strong> {data.ats_score_breakdown?.skill_score || 0}%</p>
          <p><strong>Section Completeness Score:</strong> {data.ats_score_breakdown?.section_score || 0}%</p>
          {data.missing_sections?.length > 0 && (
            <p className="text-red-600 mt-2"><strong>Missing Sections:</strong> {data.missing_sections.join(', ')}</p>
          )}
        </div>

        {data.suggestions?.length > 0 && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold mb-2">Suggestions to Improve:</h3>
            <ul className="list-disc list-inside space-y-1 text-gray-800">
              {data.suggestions.map((sug, idx) => (
                <li key={idx}>{sug}</li>
              ))}
            </ul>
          </div>
        )}

        <p className="mt-4">
          <a
            href={data.file_url}
            target="_blank"
            rel="noreferrer"
            className="text-blue-500 underline"
          >
            View Uploaded Resume ↗
          </a>
        </p>
      </div>
    </div>
  );
};

export default Result;
