import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Result = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) return navigate('/upload');
    setData(JSON.parse(stored));
  }, []);

  if (!data) return <div className="p-10 text-center">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-50 p-10">
      <h2 className="text-2xl font-bold mb-6 text-center">Resume Analysis Result</h2>

      <div className="bg-white p-6 rounded shadow-md max-w-2xl mx-auto space-y-4">
        <p><strong>Name:</strong> {data.name}</p>
        <p><strong>Education:</strong> {data.education}</p>
        <p><strong>Experience:</strong> {data.experience}</p>
        <p><strong>Skills:</strong> {data.skills?.length > 0 ? data.skills.join(', ') : 'None Detected'}</p>

        <div className="mt-4">
          <p><strong>ATS Score:</strong> <span className="text-blue-600 font-semibold">{data.ats_score}%</span></p>

          {data.ats_score_breakdown && (
            <div className="text-sm text-gray-700 mt-2">
              <p>ðŸ§  Skill Match Score: {data.ats_score_breakdown.skill_score}%</p>
              <p>ðŸ“„ Section Match Score: {data.ats_score_breakdown.section_score}%</p>
            </div>
          )}
        </div>

        {data.suggestions?.length > 0 && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold text-blue-700 mb-2">Suggestions to Improve:</h3>
            <ul className="list-disc list-inside space-y-1 text-gray-800">
              {data.suggestions.map((suggestion, idx) => (
                <li key={idx}>{suggestion}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="mt-6">
          <a
            href={data.file_url}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 underline font-medium"
          >
            ðŸ“„ View Uploaded Resume â†—
          </a>
        </div>
      </div>
    </div>
  );
};

export default Result;
