import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';

const Upload = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Get analysis type from route query (e.g., /upload?target=job)
  const [analysisType, setAnalysisType] = useState('ats');

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const target = params.get('target');
    if (target === 'job') setAnalysisType('job');
    else setAnalysisType('ats');
  }, [location]);

  const { getRootProps, getInputProps } = useDropzone({
    accept: { 'application/pdf': ['.pdf'] },
    maxFiles: 1,
    onDrop: (acceptedFiles) => {
      setError('');
      setFile(acceptedFiles[0]);
    },
  });

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a PDF resume before uploading.');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:8000/api/parse-resume', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (res.status === 200) {
        localStorage.setItem('ats-result', JSON.stringify(res.data));

        if (analysisType === 'ats') {
          navigate('/result');
        } else if (analysisType === 'job') {
          navigate('/job-eligibility-result');
        }
      } else {
        setError('Failed to analyze resume. Please try again.');
      }
    } catch (err) {
      console.error('Upload error:', err);
      setError('Server error. Please make sure your backend is running.');
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 px-4 py-12">
      <h1 className="text-2xl font-bold mb-6 text-center">Upload Your Resume (PDF)</h1>

      <div
        {...getRootProps()}
        className="border-2 border-dashed border-gray-500 bg-white p-10 w-full max-w-md text-center rounded-md cursor-pointer"
      >
        <input {...getInputProps()} />
        <p className="text-gray-600">Drag and drop your resume here, or click to upload</p>
        {file && <p className="text-green-600 font-semibold mt-3">{file.name}</p>}
      </div>

      {error && <p className="text-red-500 mt-4">{error}</p>}

      <button
        onClick={handleUpload}
        disabled={!file || loading}
        className="bg-blue-600 text-white px-6 py-2 rounded mt-6 hover:bg-blue-700 transition duration-300 disabled:bg-gray-400"
      >
        {loading ? 'Uploading...' : 'Upload and Analyze'}
      </button>
    </div>
  );
};

export default Upload;
