import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const JobEligibilityResult = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) return navigate('/job-eligibility-upload');

    try {
      const parsed = JSON.parse(stored);
      setData(parsed);
    } catch (err) {
      console.error('Invalid JSON in ats-result');
      navigate('/job-eligibility-upload');
    }
  }, []);

  if (!data) return <div className="p-10 text-center text-white">Loading...</div>;

  // Normalize skill list
  const skillSet = (data.skills || []).map(skill => skill.toLowerCase().trim());

  // Job role matching logic
  const jobRoles = [];

  const hasAll = (...skills) => skills.every(skill => skillSet.includes(skill));
  const hasSome = (...skills) => skills.some(skill => skillSet.includes(skill));

  if (hasAll('html', 'css', 'javascript', 'react')) {
    jobRoles.push('Frontend Developer');
  }

  if (hasAll('nodejs', 'express', 'mongodb')) {
    jobRoles.push('Backend Developer');
  }

  if (hasAll('html', 'css', 'javascript', 'nodejs', 'mongodb')) {
    jobRoles.push('Full Stack Developer');
  }

  if (hasSome('figma', 'ux', 'ui', 'wireframing', 'visual design')) {
    jobRoles.push('UI/UX Designer');
  }

  if (hasSome('seo', 'digital marketing', 'content strategy', 'brand development')) {
    jobRoles.push('Digital Marketing Executive');
  }

  if (hasSome('java', 'python', 'c++') && hasSome('dsa', 'problem solving')) {
    jobRoles.push('Software Engineer - Entry Level');
  }

  if (jobRoles.length === 0) {
    jobRoles.push('Intern / Trainee - Upskill for more opportunities');
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-400 text-white p-10">
      <div className="max-w-3xl mx-auto bg-white text-gray-800 p-8 rounded shadow-lg">
        <h2 className="text-3xl font-bold mb-6 text-center text-blue-700">ðŸŽ¯ Job Eligibility Results</h2>

        <div className="mb-6">
          <p><strong>Name:</strong> {data.name}</p>
          <p><strong>ATS Score:</strong> {data.ats_score}%</p>
          <p><strong>Skills:</strong> {data.skills?.join(', ') || 'None detected'}</p>
        </div>

        <h3 className="text-xl font-semibold mb-2 text-blue-700">âœ… You Are Eligible For:</h3>
        <ul className="list-disc list-inside space-y-1 text-blue-800 font-medium">
          {jobRoles.map((role, idx) => (
            <li key={idx}>{role}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default JobEligibilityResult;
