import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Result = () => {
  const [result, setResult] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) {
      navigate('/upload');
    } else {
      setResult(JSON.parse(stored));
    }
  }, []);

  if (!result) return null;

  return (
    <div className="min-h-screen flex flex-col items-center p-10 bg-gray-50">
      <div className="bg-white shadow-md rounded-lg p-8 w-full max-w-2xl">
        <h2 className="text-2xl font-bold mb-6 text-blue-700">üéØ Resume Score & Job Suggestion</h2>

        <div className="mb-4">
          <p className="text-lg font-semibold mb-1">üíº ATS Score</p>
          <div className="w-full bg-gray-200 rounded-full h-4">
            <div
              className="bg-green-500 h-4 rounded-full"
              style={{ width: `${result.ats_score || 0}%` }}
            />
          </div>
          <p className="text-sm text-gray-600 mt-1">{result.ats_score}% match with job requirements</p>
        </div>

        <div className="mb-4">
          <p className="font-semibold">üõ† Skills:</p>
          <p>{(result.skills || []).join(', ') || 'No skills detected'}</p>
        </div>

        <div className="mt-6 border-t pt-4">
          <h3 className="text-lg font-bold">Suggested Role</h3>
          <p>üë®‚Äçüíª <strong>Frontend Developer</strong></p>
          <p>üí∞ Salary Estimate: <strong>‚Çπ4.2 LPA</strong></p>
        </div>
      </div>
    </div>
  );
};

export default Result;
