import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const Upload = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const { getRootProps, getInputProps } = useDropzone({
    accept: { 'application/pdf': ['.pdf'] },
    maxFiles: 1,
    onDrop: (acceptedFiles) => setFile(acceptedFiles[0]),
  });

  const handleUpload = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:8000/api/parse-resume', formData);
      localStorage.setItem('ats-result', JSON.stringify(res.data));
      navigate('/result');
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div className="p-10 flex flex-col items-center">
      <div
        {...getRootProps()}
        className="border-dashed border-2 border-gray-400 w-full max-w-md p-10 text-center rounded cursor-pointer bg-white"
      >
        <input {...getInputProps()} />
        <p>Drag & drop your resume here, or click to select file</p>
        {file && <p className="mt-2 font-semibold text-green-600">{file.name}</p>}
      </div>
      <button
        onClick={handleUpload}
        disabled={!file || loading}
        className="bg-blue-600 text-white px-6 py-2 rounded mt-6"
      >
        {loading ? 'Uploading...' : 'Upload and Analyze'}
      </button>
    </div>
  );
};

export default Upload;
