import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Result = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) return navigate('/upload');
    setData(JSON.parse(stored));
  }, []);

  if (!data) return <div className="p-10 text-center text-lg">Loading your report...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-100 to-blue-200 p-6 md:p-10">
      <h2 className="text-3xl font-extrabold text-center text-blue-800 mb-8">ðŸŽ¯ ATS Resume Report</h2>

      <div className="bg-white max-w-3xl mx-auto p-8 rounded-2xl shadow-xl space-y-6 border border-blue-100">

        {/* Basic Info */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-gray-700">
          <p><span className="font-semibold">ðŸ‘¤ Name:</span> {data.name}</p>
          <p><span className="font-semibold">ðŸŽ“ Education:</span> {data.education}</p>
          <p><span className="font-semibold">ðŸ’¼ Experience:</span> {data.experience}</p>
          <p>
            <span className="font-semibold">ðŸ§  Skills:</span>{" "}
            {data.skills.length > 0 ? data.skills.join(", ") : "None Detected"}
          </p>
        </div>

        {/* ATS Score */}
        <div className="mt-4 text-center">
          <p className="text-lg font-bold text-blue-700">
            âœ… ATS Score: <span className="text-2xl">{data.ats_score}%</span>
          </p>
          <p className="text-sm mt-1 text-gray-600">
            ðŸŽ¯ Skills Match: {data.ats_score_breakdown.skill_score}% &nbsp; | &nbsp; ðŸ§© Section Coverage: {data.ats_score_breakdown.section_score}%
          </p>
        </div>

        {/* Suggestions */}
        {data.suggestions?.length > 0 && (
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 rounded-md mt-6">
            <h3 className="font-semibold text-yellow-700 mb-2">ðŸ“Œ Suggestions to Improve Your Resume:</h3>
            <ul className="list-disc list-inside text-sm text-gray-800 space-y-1">
              {data.suggestions.map((sug, idx) => (
                <li key={idx}>{sug}</li>
              ))}
            </ul>
          </div>
        )}

        {/* View Resume */}
        <p className="mt-6 text-center">
          <a
            href={data.file_url}
            target="_blank"
            rel="noreferrer"
            className="inline-block bg-blue-600 hover:bg-blue-700 text-white font-semibold px-5 py-2 rounded-lg shadow transition"
          >
            ðŸ“„ View Uploaded Resume
          </a>
        </p>
      </div>
    </div>
  );
};

export default Result;
