import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const JobEligibilityResult = () => {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (!stored) return navigate('/upload');

    const parsed = JSON.parse(stored);
    setData(parsed);
  }, []);

  if (!data) return <div className="p-10 text-center">Loading...</div>;

  // Sample logic to suggest job types
  const suggestedRoles = [];

  if (data.matched_by_category?.technical?.length > 5) suggestedRoles.push('Full Stack Developer');
  if (data.matched_by_category?.design?.length > 4) suggestedRoles.push('UI/UX Designer');
  if (data.matched_by_category?.marketing?.length > 4) suggestedRoles.push('Digital Marketer');

  return (
    <div className="min-h-screen bg-blue-50 p-8">
      <h2 className="text-2xl font-bold mb-4">Eligible Job Roles</h2>

      <div className="bg-white p-6 rounded shadow max-w-xl mx-auto">
        {suggestedRoles.length > 0 ? (
          <>
            <p className="mb-2 text-gray-700">Based on your resume, you may be suited for:</p>
            <ul className="list-disc list-inside text-blue-700 font-medium">
              {suggestedRoles.map((role, idx) => (
                <li key={idx}>{role}</li>
              ))}
            </ul>
          </>
        ) : (
          <p className="text-red-600">We couldn't confidently match you to a job role. Please improve your resume.</p>
        )}
      </div>
    </div>
  );
};

export default JobEligibilityResult;
