import { useEffect, useState } from 'react';

const JobEligibility = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (stored) {
      setData(JSON.parse(stored));
    }
  }, []);

  if (!data) return <div className="p-10 text-center text-white">Loading...</div>;

  // A very basic mock job eligibility filter logic
  const getEligibleJobs = () => {
    const skillSet = data.skills.map(s => s.toLowerCase());
    const jobs = [];

    if (skillSet.includes("react") && skillSet.includes("nodejs")) {
      jobs.push("Frontend Developer");
    }
    if (skillSet.includes("python") && skillSet.includes("django")) {
      jobs.push("Backend Developer");
    }
    if (skillSet.includes("figma") || skillSet.includes("ui/ux")) {
      jobs.push("UI/UX Designer");
    }
    if (skillSet.includes("seo") || skillSet.includes("digital marketing")) {
      jobs.push("Digital Marketing Executive");
    }
    if (skillSet.includes("mongodb") && skillSet.includes("flask")) {
      jobs.push("Full Stack Developer");
    }

    if (jobs.length === 0) {
      jobs.push("Intern / Entry-level Trainee (expand your skillset to unlock more jobs)");
    }

    return jobs;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-400 text-white p-10">
      <div className="max-w-3xl mx-auto bg-white text-gray-800 p-8 rounded shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center">üîç Job Eligibility Report</h2>

        <p className="mb-2"><strong>Name:</strong> {data.name}</p>
        <p className="mb-2"><strong>Detected Skills:</strong> {data.skills.join(', ') || 'None Detected'}</p>
        <p className="mb-6"><strong>ATS Score:</strong> {data.ats_score}%</p>

        <h3 className="text-xl font-semibold mb-2">üìå Suggested Roles:</h3>
        <ul className="list-disc list-inside">
          {getEligibleJobs().map((job, idx) => (
            <li key={idx} className="text-blue-700">{job}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default JobEligibility;
