import { useEffect, useState } from 'react';

const JobEligibility = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const stored = localStorage.getItem('ats-result');
    if (stored) {
      setData(JSON.parse(stored));
    }
  }, []);

  if (!data) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-blue-600 text-white text-lg">
        Loading eligibility report...
      </div>
    );
  }

  const getEligibleJobs = () => {
    const skillSet = data.skills.map((s) => s.toLowerCase());
    const jobs = [];

    if (skillSet.includes("react") && skillSet.includes("nodejs")) {
      jobs.push("Frontend Developer");
    }
    if (skillSet.includes("python") && skillSet.includes("django")) {
      jobs.push("Backend Developer");
    }
    if (
      skillSet.includes("figma") ||
      skillSet.includes("ui/ux") ||
      skillSet.includes("ux") ||
      skillSet.includes("ui")
    ) {
      jobs.push("UI/UX Designer");
    }
    if (
      skillSet.includes("seo") ||
      skillSet.includes("digital marketing") ||
      skillSet.includes("content strategy")
    ) {
      jobs.push("Digital Marketing Executive");
    }
    if (
      skillSet.includes("mongodb") &&
      (skillSet.includes("flask") || skillSet.includes("nodejs"))
    ) {
      jobs.push("Full Stack Developer");
    }

    if (jobs.length === 0) {
      jobs.push(
        "Intern / Entry-level Trainee (add more relevant skills and experience to unlock more roles)"
      );
    }

    return jobs;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-400 text-white px-4 py-10">
      <div className="max-w-3xl mx-auto bg-white text-gray-800 p-8 rounded-xl shadow-lg">
        <h2 className="text-3xl font-bold mb-6 text-center text-blue-700">
          üîç Job Eligibility Report
        </h2>

        <div className="mb-4">
          <p><span className="font-semibold">üë§ Name:</span> {data.name}</p>
          <p><span className="font-semibold">üß† Detected Skills:</span> {data.skills.length > 0 ? data.skills.join(', ') : 'None Detected'}</p>
          <p><span className="font-semibold">üìä ATS Score:</span> <span className="text-blue-600 font-semibold">{data.ats_score}%</span></p>
        </div>

        <h3 className="text-xl font-semibold mb-2 text-blue-800">üìå Suggested Roles:</h3>
        <ul className="list-disc list-inside space-y-1">
          {getEligibleJobs().map((job, idx) => (
            <li key={idx} className="text-blue-700">{job}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default JobEligibility;
